let input = `



`


// let input =`RRU`

input = input.split('\n')

console.log(input);

let keypad = [["0","0","D","0","0"],["0","A","B","C","0"],["5","6","7","8","9"],["0","2","3","4","0"],["0","0","1","0","0"]]
let currButtCoords = [2,0]

for (var i = 0; i < input.length; i++) {
  input[i].split('')
  for (instruction of input[i]) {
    let vec = [0,0]
    switch (instruction) {
      case "U":
        vec = [1,0]
        break;
      case "D":
        vec = [-1,0]
        break;
      case "L":
      vec = [0,-1]
        break;
      case "R":
      vec = [0,1]
        break;
      default:

    }
    let newButtonCoords = addVecs(currButtCoords,vec)
    currButtCoords = boundCheck(newButtonCoords, vec)
  }
  console.log(`i is ${i} press ${keypad[currButtCoords[0]][currButtCoords[1]]}`);
}

function addVecs(vec1, vec2) {
  return [vec1[0]+vec2[0], vec1[1] + vec2[1]]
}

function boundCheck(coords, vec) {
  let newcoords = coords
  if (coords[0] < 0 || coords[0] > 4 || keypad[coords[0]][coords[1]] == "0")
    newcoords[0] = coords[0] - vec[0]
  if (coords[1] < 0 || coords[1] > 4 || keypad[coords[0]][coords[1]] == "0")
    newcoords[1] = coords[1] - vec[1]
  return newcoords
}
